                "c:majorGridlines": {},
                "c:numFmt": {
                  "@formatCode": formatCode,
                  "@sourceLinked": "0"
                }
              }
            }
          }
        }
      }
    },

    /// @brief returns XML snippet for an axis scale
    /// currently just min/max are supported
    /*
      <c:scaling><c:orientation val="minMax"/>
        <c:max val="24.0"/>
        <c:min val="24.0"/>
      </c:scaling>
    */
    _scaling: function (minValue, maxValue) {
      var ref = {
        "c:scaling": {
          "c:orientation": {
            "@val": "minMax"
          }
        }
      };
